'use client';

import Link from 'next/link';
import { usePathname } from 'next/navigation';
import { useAuth } from '@/context/AuthContext';
import { useLanguage } from '@/contexts/LanguageContext';
import {
    LayoutDashboard,
    ShoppingBag,
    Tags,
    ClipboardList,
    Users,
    LogOut,
    Menu,
    X,
    Search,
    Bell,
    Moon,
    Sun,
    Settings,
    MessageSquare,
    Globe
} from 'lucide-react';
import { useState, useEffect } from 'react';

export default function AdminLayout({ children }: { children: React.ReactNode }) {
    const pathname = usePathname();
    const { logout, user } = useAuth();
    const { t, language, setLanguage, isRTL } = useLanguage();
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);
    const [isDarkMode, setIsDarkMode] = useState(false);

    const menuItems = [
        { name: t('dashboard'), path: '/admin', icon: LayoutDashboard },
        { name: t('products'), path: '/admin/products', icon: ShoppingBag },
        { name: t('categories'), path: '/admin/categories', icon: Tags },
        { name: t('orders'), path: '/admin/orders', icon: ClipboardList },
        { name: t('users'), path: '/admin/users', icon: Users },
        { name: t('messages'), path: '/admin/messages', icon: MessageSquare },
        { name: 'Avis Clients', path: '/admin/reviews', icon: MessageSquare }, // Using MessageSquare temporarily or another icon if available
    ];

    // Initialize theme from localStorage or system preference
    useEffect(() => {
        const savedTheme = localStorage.getItem('theme');
        if (savedTheme === 'dark' || (!savedTheme && window.matchMedia('(prefers-color-scheme: dark)').matches)) {
            setIsDarkMode(true);
        }
    }, []);

    // Toggle theme
    const toggleTheme = () => {
        const newTheme = !isDarkMode;
        setIsDarkMode(newTheme);
        localStorage.setItem('theme', newTheme ? 'dark' : 'light');
    };

    const toggleLanguage = () => {
        setLanguage(language === 'fr' ? 'ar' : 'fr');
    };

    return (
        <div className={`min-h-screen transition-colors duration-300 ${isDarkMode ? 'dark bg-gray-900' : 'bg-gray-100'}`} dir={isRTL ? 'rtl' : 'ltr'}>
            <style jsx global>{`
                :root {
                    --primary-orange: #FF8C42;
                    --primary-green: #4CAF50;
                }
                .dark {
                    color-scheme: dark;
                }
            `}</style>

            <div className={`flex h-screen overflow-hidden ${isDarkMode ? 'text-gray-100' : 'text-gray-800'}`}>
                {/* Sidebar */}
                <aside className={`
                    fixed inset-y-0 ${isRTL ? 'right-0 border-l' : 'left-0 border-r'} z-50 w-64 ${isDarkMode ? 'bg-gray-800 border-gray-700' : 'bg-white border-gray-200'} transform transition-transform duration-300 ease-in-out
                    lg:relative lg:translate-x-0
                    ${isSidebarOpen ? 'translate-x-0' : (isRTL ? 'translate-x-full' : '-translate-x-full')}
                `}>
                    <div className="h-full flex flex-col">
                        {/* Logo */}
                        <div className={`h-20 flex items-center px-6 border-b ${isDarkMode ? 'border-gray-700' : 'border-gray-100'}`}>
                            <Link href="/admin" className="flex items-center gap-2">
                                <div className="w-8 h-8 rounded-lg bg-[var(--primary-orange)] flex items-center justify-center text-white font-bold text-xl">
                                    T
                                </div>
                                <span className={`text-xl font-bold ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                                    TABAKH <span className="text-[var(--primary-orange)]">ADMIN</span>
                                </span>
                            </Link>
                            <button className="lg:hidden ms-auto" onClick={() => setIsSidebarOpen(false)}>
                                <X size={24} />
                            </button>
                        </div>

