'use client';

import { useEffect, useState } from 'react';
import { Star, User, Quote } from 'lucide-react';
import { reviewsAPI } from '@/lib/api';

export default function ReviewList() {
    const [reviews, setReviews] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchReviews = async () => {
            try {
                const res = await reviewsAPI.getApproved();
                setReviews(res.data || []);
            } catch (error) {
                console.error('Error fetching reviews:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchReviews();
    }, []);

    if (loading) {
        return (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 animate-pulse">
                {[1, 2, 3].map((i) => (
                    <div key={i} className="bg-gray-100 h-64 rounded-2xl"></div>
                ))}
            </div>
        );
    }

    if (reviews.length === 0) {
        return (
            <div className="text-center py-12 bg-gray-50 rounded-2xl">
                <Quote className="mx-auto text-gray-300 mb-4" size={48} />
                <p className="text-gray-500 font-medium">Aucun avis pour le moment. Soyez le premier !</p>
            </div>
        );
    }

    return (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {reviews.map((review) => (
                <div key={review._id} className="bg-white p-6 rounded-2xl shadow-sm border border-gray-100 hover:shadow-md transition-shadow relative overflow-hidden group">
                    <div className="absolute top-0 right-0 p-4 opacity-10 group-hover:opacity-20 transition-opacity">
                        <Quote size={40} className="text-orange-500" />
                    </div>

                    <div className="flex items-center gap-4 mb-4">
                        <div className="w-12 h-12 rounded-full bg-orange-100 flex items-center justify-center text-orange-600 font-bold text-lg">
                            {review.name.charAt(0).toUpperCase()}
                        </div>
                        <div>
                            <h4 className="font-bold text-gray-900">{review.name}</h4>
                            <div className="flex text-orange-400">
                                {[...Array(5)].map((_, i) => (
                                    <Star
                                        key={i}
                                        size={14}
                                        fill={i < review.rating ? "currentColor" : "none"}
                                        className={i < review.rating ? "text-orange-400" : "text-gray-300"}
                                    />
                                ))}
                            </div>
                        </div>
                    </div>

                    <p className="text-gray-600 leading-relaxed italic relative z-10">
                        "{review.comment}"
                    </p>

                    <div className="mt-4 pt-4 border-t border-gray-50 text-xs text-gray-400 font-medium">
                        {new Date(review.createdAt).toLocaleDateString('fr-FR', {
                            year: 'numeric',
                            month: 'long',
                            day: 'numeric'
                        })}
                    </div>
                </div>
            ))}
        </div>
    );
}

'use client';

import { useState, useEffect } from 'react';
import Image from 'next/image';
import { reviewsAPI } from '@/lib/api';

export default function TestimonialSlider({ initialTestimonials, language }: { initialTestimonials: any[], language: string }) {
    const [currentIndex, setCurrentIndex] = useState(0);
    const [reviews, setReviews] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchReviews = async () => {
            try {
                const res = await reviewsAPI.getApproved();
                if (res.data && res.data.length > 0) {
                    setReviews(res.data);
                } else {
                    setReviews(initialTestimonials); // Fallback to provided hardcoded ones
                }
            } catch (error) {
                console.error('Error fetching reviews:', error);
                setReviews(initialTestimonials);
            } finally {
                setLoading(false);
            }
        };
        fetchReviews();
    }, [initialTestimonials]);

    useEffect(() => {
        if (reviews.length === 0) return;
        const interval = setInterval(() => {
            setCurrentIndex((prev) => (prev + 1) % reviews.length);
        }, 5000);
        return () => clearInterval(interval);
    }, [reviews.length]);

    if (loading || reviews.length === 0) return null;

    const currentReview = reviews[currentIndex];

    // Normalize data structure between API review and local testimonial
    // Local: { name, date, textFr, textAr, image }
    // API: { name, createdAt, comment, user: { name? }, rating }

    const displayText = currentReview.comment || (language === 'ar' ? currentReview.textAr : currentReview.textFr);
    const displayDate = currentReview.createdAt
        ? new Date(currentReview.createdAt).toLocaleDateString(language === 'ar' ? 'ar-DZ' : 'fr-FR', { day: 'numeric', month: 'short' })
        : currentReview.date;
    const displayName = currentReview.name;
    const displayImage = currentReview.image || `https://ui-avatars.com/api/?name=${encodeURIComponent(displayName)}&background=ff8c42&color=fff`;

    return (
        <div className="bg-gray-800/80 backdrop-blur-sm rounded-lg p-8 md:p-12 transition-all duration-500">
            <div className="flex items-start gap-6 mb-6">
                <div className="relative w-16 h-16 rounded-full overflow-hidden flex-shrink-0">
                    <img
                        src={displayImage}
                        alt={displayName}
                        className="w-full h-full object-cover"
                    />
                </div>
                <div>
                    <h4 className="text-white font-bold text-xl">
                        {displayName}
                    </h4>
                    <p className="text-gray-400">{displayDate}</p>
                </div>
            </div>

            <p className="text-white text-lg italic leading-relaxed mb-6 min-h-[100px]">
                "{displayText}"
            </p>

            {/* Dots Navigation */}
            <div className="flex justify-center gap-2">
                {reviews.map((_, index) => (
                    <button
                        key={index}
                        onClick={() => setCurrentIndex(index)}
                        className={`w-3 h-3 rounded-full transition-all ${index === currentIndex
                            ? 'bg-[#FF8C42] w-8'
                            : 'bg-gray-500'
                            }`}
                    />
                ))}
            </div>
        </div>
    );
}

'use client';

import { useState } from 'react';
import { Tags, Image as ImageIcon, Check } from 'lucide-react';

interface CategoryFormProps {
    initialData?: any;
    onSubmit: (data: FormData) => Promise<void>;
    onCancel: () => void;
    loading: boolean;
}

export default function CategoryForm({ initialData, onSubmit, onCancel, loading }: CategoryFormProps) {
    const [formData, setFormData] = useState({
        name: initialData?.name || '',
        description: initialData?.description || '',
        isActive: initialData?.isActive !== undefined ? initialData.isActive : true
    });
    const [image, setImage] = useState<File | null>(null);
    const [imagePreview, setImagePreview] = useState(initialData?.image || '');

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value, type } = e.target as HTMLInputElement;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? (e.target as HTMLInputElement).checked : value
        }));
    };

    const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (file) {
            setImage(file);
            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result as string);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        const data = new FormData();
        Object.keys(formData).forEach(key => {
            data.append(key, formData[key as keyof typeof formData].toString());
        });
        if (image) {
            data.append('image', image);
        }
        await onSubmit(data);
    };

